import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../context';
import { motion } from 'framer-motion';
import { Link, useNavigate } from 'react-router-dom';
import gsap from 'gsap';
import { useAuth } from '../AuthContext';
import axios from 'axios';

interface Props {
  type: 'signin' | 'signup';
}

const Auth: React.FC<Props> = ({ type }) => {
  const { isDarkMode } = useTheme();
  const { login } = useAuth();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const containerRef = useRef(null);

  useEffect(() => {
    const ctx = gsap.context(() => {
      gsap.to(".auth-glow", {
        x: "random(-100, 100)",
        y: "random(-100, 100)",
        duration: 8,
        repeat: -1,
        yoyo: true,
        ease: "sine.inOut",
        stagger: 2
      });
    }, containerRef);
    return () => ctx.revert();
  }, []);

  const handleAuth = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      const endpoint = type === 'signin' ? '/api/auth/login' : '/api/auth/register';
      const baseUrl = import.meta.env.VITE_API_BASE_URL || 'http://localhost:5000';
      const response = await axios.post(`${baseUrl}${endpoint}`, formData);

      if (response.data.success) {
        login(response.data);
        navigate('/lms');
      }
    } catch (err: any) {
      setError(err.response?.data?.message || 'Authentication failed');
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleAuth = () => {
    setIsLoading(true);
    setTimeout(() => {
      alert("Google Login is not yet configured with Backend. Please use Email/Password.");
      setIsLoading(false);
    }, 1500);
  };

  return (
    <div
      ref={containerRef}
      className="h-screen relative flex flex-col items-center justify-center p-4 md:p-6 pt-20 md:pt-32 overflow-hidden"
    >
      {/* Dynamic Background Accents */}
      <div className={`auth-glow absolute top-0 left-0 w-[500px] h-[500px] blur-[150px] rounded-full opacity-10 pointer-events-none transition-colors duration-1000 ${isDarkMode ? 'bg-cyan-500' : 'bg-blue-200'}`}></div>
      <div className={`auth-glow absolute bottom-0 right-0 w-[500px] h-[500px] blur-[150px] rounded-full opacity-10 pointer-events-none transition-colors duration-1000 ${isDarkMode ? 'bg-fuchsia-500' : 'bg-rose-200'}`}></div>

      {/* Horizontal Auth Card */}
      <motion.div
        key={type}
        initial={{ opacity: 0, scale: 0.98, y: 10 }}
        animate={{ opacity: 1, scale: 1, y: 0 }}
        transition={{ duration: 0.6, ease: [0.16, 1, 0.3, 1] }}
        className={`relative z-10 w-full max-w-[1000px] flex flex-col md:flex-row rounded-[2.5rem] md:rounded-[3.5rem] overflow-hidden refractive-border liquid-glass border shadow-2xl max-h-[80vh] md:max-h-[75vh] ${isDarkMode ? 'bg-zinc-950/40 border-white/5' : 'bg-white/40 border-white/20'
          }`}
      >
        {/* Left Side: Branding/Visual */}
        <div className={`hidden md:flex md:w-5/12 p-8 lg:p-12 flex-col justify-between relative overflow-hidden border-r ${isDarkMode ? 'border-white/5' : 'border-black/5'}`}>
          <div className="relative z-10 space-y-6">
            <div>
              <h2 className="text-3xl lg:text-5xl font-black font-syne tracking-tighter leading-[0.9] uppercase italic mb-4">
                {type === 'signin' ? 'ELEVATE \nYOUR \nLOGIC.' : 'JOIN \nTHE \nELITE.'}
              </h2>
              <p className="text-zinc-500 font-bold uppercase tracking-widest text-[9px] max-w-[200px]">
                {type === 'signin' ? 'Resume your learning track and access workspace.' : 'Register for verified tracks and mentorship.'}
              </p>
            </div>

            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.3 }}
            >
              <Link to="/" className="flex items-center gap-4 group">
                <div className="h-14 w-14 rounded-2xl bg-zinc-900/10 dark:bg-zinc-100/10 p-2 overflow-hidden transition-transform group-hover:scale-110">
                  <img src="/Logo2.jpeg" alt="WorknAI Official Brand" className="w-full h-full object-contain" />
                </div>
              </Link>
            </motion.div>
          </div>
        </div>

        {/* Right Side: Form */}
        <div className="w-full md:w-7/12 p-6 md:p-10 lg:p-12 flex flex-col justify-center overflow-y-auto">
          <div className="mb-6">
            <h3 className="text-xl font-black font-syne uppercase tracking-tight italic">
              {type === 'signin' ? 'Student Sign In' : 'New Enrollment'}
            </h3>
            <div className={`w-10 h-1 mt-2 rounded-full bg-gradient-to-r ${isDarkMode ? 'from-cyan-400 to-blue-500' : 'from-blue-600 to-emerald-500'}`}></div>
          </div>

          <form onSubmit={handleAuth} className="space-y-4 md:space-y-5">
            {error && (
              <div className="p-3 rounded-xl bg-red-500/10 border border-red-500/20 text-red-500 text-[10px] font-bold uppercase tracking-wider text-center">
                {error}
              </div>
            )}

            <button
              type="button"
              onClick={handleGoogleAuth}
              disabled={isLoading}
              className={`group relative overflow-hidden w-full h-14 md:h-16 flex items-center justify-center gap-3 rounded-[1.2rem] font-black text-[10px] uppercase tracking-[0.2em] transition-all transform hover:scale-[1.01] active:scale-95 border-2 ${isDarkMode ? 'bg-zinc-900/50 border-white/5 hover:border-cyan-500/30' : 'bg-white/50 border-black/5 hover:border-black'
                }`}
            >
              <svg className="w-5 h-5" viewBox="0 0 48 48">
                <path fill="#EA4335" d="M24 9.5c3.54 0 6.71 1.22 9.21 3.6l6.85-6.85C35.9 2.38 30.47 0 24 0 14.62 0 6.51 5.38 2.56 13.22l7.98 6.19C12.43 13.72 17.74 9.5 24 9.5z" />
                <path fill="#4285F4" d="M46.98 24.55c0-1.57-.15-3.09-.38-4.55H24v9.02h12.94c-.58 2.96-2.26 5.48-4.78 7.18l7.73 6c4.51-4.18 7.09-10.36 7.09-17.65z" />
                <path fill="#FBBC05" d="M10.53 28.59c-.48-1.45-.76-2.99-.76-4.59s.27-3.14.76-4.59l-7.98-6.19C.92 16.46 0 20.12 0 24c0 3.88.92 7.54 2.56 10.78l7.97-6.19z" />
                <path fill="#34A853" d="M24 48c6.48 0 11.93-2.13 15.89-5.81l-7.73-6c-2.15 1.45-4.92 2.3-8.16 2.3-6.26 0-11.57-4.22-13.47-9.91l-7.98 6.19C6.51 42.62 14.62 48 24 48z" />
                <path fill="none" d="M0 0h48v48H0z" />
              </svg>
              Google Login
            </button>

            <div className="flex items-center gap-4">
              <div className="flex-grow h-[1px] bg-zinc-500/10"></div>
              <span className="text-[8px] font-black text-zinc-500 uppercase tracking-[0.3em]">OR EMAIL</span>
              <div className="flex-grow h-[1px] bg-zinc-500/10"></div>
            </div>

            <div className="grid grid-cols-1 gap-3 md:gap-4">
              {type === 'signup' && (
                <div>
                  <label className="block text-[8px] font-black uppercase tracking-[0.2em] text-zinc-500 mb-1.5 ml-1">Full Name</label>
                  <input
                    type="text"
                    required
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    placeholder="ALEX MERCER"
                    className={`w-full h-12 md:h-14 px-5 rounded-[1rem] border-2 outline-none transition-all font-bold text-xs ${isDarkMode
                      ? 'bg-zinc-950/50 border-zinc-800 focus:border-cyan-500/50 text-white placeholder-zinc-800'
                      : 'bg-zinc-50 border-zinc-100 focus:border-black placeholder-zinc-300'
                      }`}
                  />
                </div>
              )}
              <div>
                <label className="block text-[8px] font-black uppercase tracking-[0.2em] text-zinc-500 mb-1.5 ml-1">Email</label>
                <input
                  type="email"
                  required
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  placeholder="LEARNER@WORKNAI.TECH"
                  className={`w-full h-12 md:h-14 px-5 rounded-[1rem] border-2 outline-none transition-all font-bold text-xs ${isDarkMode
                    ? 'bg-zinc-950/50 border-zinc-800 focus:border-cyan-500/50 text-white placeholder-zinc-800'
                    : 'bg-zinc-50 border-zinc-100 focus:border-black placeholder-zinc-300'
                    }`}
                />
              </div>
              <div>
                <label className="block text-[8px] font-black uppercase tracking-[0.2em] text-zinc-500 mb-1.5 ml-1">Password</label>
                <input
                  type="password"
                  required
                  value={formData.password}
                  onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                  placeholder="••••••••"
                  className={`w-full h-12 md:h-14 px-5 rounded-[1rem] border-2 outline-none transition-all font-bold text-xs ${isDarkMode
                    ? 'bg-zinc-950/50 border-zinc-800 focus:border-cyan-500/50 text-white placeholder-zinc-800'
                    : 'bg-zinc-50 border-zinc-100 focus:border-black placeholder-zinc-300'
                    }`}
                />
              </div>

              <button
                type="submit"
                disabled={isLoading}
                className={`w-full py-4 md:py-5 mt-2 rounded-[1.2rem] font-black text-xs md:text-sm uppercase tracking-widest transition-all transform hover:scale-[1.01] active:scale-95 shadow-xl ${isDarkMode
                  ? 'bg-cyan-500 text-white hover:bg-cyan-400 shadow-cyan-900/20'
                  : 'bg-black text-white hover:bg-zinc-800 shadow-black/20'
                  }`}
              >
                {isLoading ? 'Processing...' : (type === 'signin' ? 'Verify & Enter' : 'Start Education')}
              </button>
            </div>
          </form>

          <div className="mt-4 flex flex-col sm:flex-row justify-between items-center gap-2 px-1">
            <p className="text-[9px] font-bold text-zinc-500 uppercase tracking-widest text-center sm:text-left">
              {type === 'signin' ? "No account?" : "Already enrolled?"}
              <Link
                to={type === 'signin' ? '/signup' : '/signin'}
                className={`ml-2 font-black transition-colors ${isDarkMode ? 'text-cyan-400 hover:text-white' : 'text-blue-600 hover:text-black'}`}
              >
                {type === 'signin' ? 'Apply' : 'Login'}
              </Link>
            </p>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default Auth;

import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext';
import { useTheme, useData } from '../context';
import { motion } from 'framer-motion';
import axios from 'axios';
import { CreditCard, ShieldCheck, ArrowRight, ChevronLeft, Lock } from 'lucide-react';
import { useNotification } from '../NotificationContext';

const Checkout: React.FC = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { user } = useAuth();
    const { isDarkMode } = useTheme();
    const { courses } = useData();
    const { showToast } = useNotification();
    const [loading, setLoading] = useState(false);
    const [success, setSuccess] = useState(false);

    const course = courses.find(c => c.id === id || c._id === id);

    if (!course) {
        return (
            <div className={`min-h-screen flex items-center justify-center ${isDarkMode ? 'bg-zinc-950 text-white' : 'bg-white text-black'}`}>
                <p className="text-zinc-500 font-bold uppercase tracking-widest text-[10px]">Course not found</p>
            </div>
        );
    }

    const gst = Math.round(course.discountedPrice * 0.18);
    const total = course.discountedPrice + gst;

    const handlePayment = async () => {
        setLoading(true);
        try {
            const baseUrl = import.meta.env.VITE_API_BASE_URL || 'http://localhost:5000';
            const res = await axios.post(`${baseUrl}/api/user/purchase`, { courseId: course.id || course._id }, {
                headers: { Authorization: `Bearer ${user?.token}` }
            });
            if (res.data.success) {
                setSuccess(true);
                setTimeout(() => navigate('/lms/courses'), 2000);
            }
        } catch (err: any) {
            showToast(err.response?.data?.message || "Payment failed", "error");
        } finally {
            setLoading(false);
        }
    };

    if (success) {
        return (
            <div className={`min-h-screen flex flex-col items-center justify-center text-center p-6 ${isDarkMode ? 'bg-zinc-950 text-white' : 'bg-white text-black'}`}>
                <motion.div
                    initial={{ scale: 0.8, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    className="h-24 w-24 rounded-full bg-emerald-500/20 flex items-center justify-center mb-8"
                >
                    <ShieldCheck size={48} className="text-emerald-500" />
                </motion.div>
                <h1 className="text-4xl font-bold mb-4">Payment Confirmed</h1>
                <p className="text-zinc-500 font-bold text-[10px] uppercase tracking-[0.3em]">Welcome to the track. Redirecting to your archive...</p>
            </div>
        );
    }

    return (
        <div className={`min-h-screen pt-32 pb-24 px-6 md:px-12 ${isDarkMode ? 'bg-zinc-950 text-white' : 'bg-zinc-50 text-black'}`}>
            <div className="max-w-6xl mx-auto">
                <button onClick={() => navigate(-1)} className="flex items-center gap-2 text-zinc-500 hover:text-white transition-colors mb-12 font-bold text-[10px] uppercase tracking-widest">
                    <ChevronLeft size={16} /> Back to Browse
                </button>

                <div className="grid grid-cols-1 lg:grid-cols-2 gap-16">
                    {/* Left: Summary */}
                    <div className="space-y-8">
                        <div className="flex flex-col gap-2">
                            <h1 className="text-5xl font-bold italic tracking-tighter">Finalize Enrollment</h1>
                            <p className="text-zinc-500 font-bold uppercase text-[10px] tracking-widest">Digital Terminal • Secure Checkout</p>
                        </div>

                        <div className={`p-8 rounded-[3rem] border ${isDarkMode ? 'bg-zinc-900/50 border-white/5' : 'bg-white border-black/5'} relative overflow-hidden group`}>
                            <div className="relative z-10 flex gap-6">
                                <div className="h-24 w-40 rounded-2xl overflow-hidden shrink-0 bg-zinc-800">
                                    <img src={course.certificateImage || 'https://images.unsplash.com/photo-1635070041078-e363dbe005cb?q=80&w=2070&auto=format&fit=crop'} className="w-full h-full object-cover" alt="" />
                                </div>
                                <div className="flex flex-col justify-center">
                                    <h2 className="text-2xl font-bold mb-2">{course.name}</h2>
                                    <p className="text-zinc-500 font-bold text-[10px] uppercase tracking-widest">{course.language} • Professional Track</p>
                                </div>
                            </div>
                        </div>

                        <div className="space-y-4">
                            <div className="flex justify-between items-center text-zinc-500 font-bold text-[10px] uppercase tracking-widest px-4">
                                <span>Tuition Fee</span>
                                <span>₹{course.discountedPrice?.toLocaleString()}</span>
                            </div>
                            <div className="flex justify-between items-center text-zinc-500 font-bold text-[10px] uppercase tracking-widest px-4 pb-4 border-b border-white/5">
                                <span>Regulatory GST (18%)</span>
                                <span>₹{gst.toLocaleString()}</span>
                            </div>
                            <div className="flex justify-between items-center px-4 pt-4">
                                <span className="text-xl font-bold">Total Investment</span>
                                <span className="text-3xl font-bold text-cyan-400 italic">₹{total.toLocaleString()}</span>
                            </div>
                        </div>
                    </div>

                    {/* Right: Payment Method Mocks */}
                    <div className="space-y-8">
                        <div className={`p-10 rounded-[4rem] border ${isDarkMode ? 'bg-zinc-900/50 border-white/10' : 'bg-white border-black/10'}`}>
                            <div className="flex items-center gap-3 mb-10">
                                <Lock size={18} className="text-emerald-500" />
                                <span className="text-[10px] font-bold text-emerald-500 uppercase tracking-widest">AES-256 Encrypted Payment Gateway</span>
                            </div>

                            <div className="space-y-6">
                                <div className="p-6 rounded-3xl border-2 border-cyan-500 bg-cyan-500/5 flex items-center justify-between">
                                    <div className="flex items-center gap-4">
                                        <CreditCard className="text-cyan-400" />
                                        <div>
                                            <p className="text-xs font-bold uppercase tracking-widest">Card / UPI / Netbanking</p>
                                            <p className="text-[8px] text-zinc-500 font-bold uppercase tracking-widest">Powered by Razorpay Secure</p>
                                        </div>
                                    </div>
                                    <div className="h-4 w-4 rounded-full border-4 border-cyan-500 bg-black"></div>
                                </div>

                                <div className="space-y-4 pt-6">
                                    <button
                                        onClick={handlePayment}
                                        disabled={loading}
                                        className={`w-full py-6 rounded-3xl font-bold text-[12px] uppercase tracking-[0.2em] transform transition-all hover:scale-[1.02] active:scale-[0.98] flex items-center justify-center gap-3 shadow-2xl disabled:opacity-50 ${isDarkMode ? 'bg-white text-black shadow-white/10' : 'bg-black text-white shadow-black/10'}`}
                                    >
                                        {loading ? 'Processing Transaction...' : 'Complete Payment'} <ArrowRight size={18} />
                                    </button>
                                    <div className="flex items-center justify-center gap-4 opacity-30 grayscale">
                                        <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/2/24/Visa.svg/1200px-Visa.svg.png" className="h-4" alt="" />
                                        <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/2/2a/Mastercard-logo.svg/1280px-Mastercard-logo.svg.png" className="h-4" alt="" />
                                        <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/e/e1/UPI-Logo-vector.svg/1200px-UPI-Logo-vector.svg.png" className="h-4" alt="" />
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="flex items-start gap-4 p-8 opacity-50">
                            <ShieldCheck size={24} className="text-zinc-500 shrink-0" />
                            <p className="text-[8px] font-bold leading-relaxed text-zinc-600 uppercase tracking-widest">
                                By completing this transaction, you agree to the WorknAI Terms of Service. Purchases are final and granted immediate access to the professional track repository.
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Checkout;

import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import {
    ChevronRight,
    ChevronDown,
    MapPin,
    Clock,
    Globe,
    Award,
    BarChart,
    CheckCircle2,
    ArrowRight
} from "lucide-react";
import { useTheme } from "../context";
import coursesApi, { type CourseDetail as CourseDetailType, getCourseStatuses } from "../../services/api";

const CourseDetail: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const { isDarkMode } = useTheme();
    const [course, setCourse] = useState<CourseDetailType | null>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [openPhaseIndex, setOpenPhaseIndex] = useState<number>(0);

    useEffect(() => {
        const fetchCourse = async () => {
            if (!id) return;
            try {
                setLoading(true);
                const data = await coursesApi.getCourseById(id);
                setCourse(data);
            } catch (err) {
                setError(err instanceof Error ? err.message : "Course not found");
            } finally {
                setLoading(false);
            }
        };
        fetchCourse();
    }, [id]);

    if (loading) {
        return (
            <div className={`min-h-screen flex items-center justify-center ${isDarkMode ? "bg-black" : "bg-white"}`}>
                <div className="text-center">
                    <div className={`w-12 h-12 border-4 border-t-transparent animate-spin rounded-full mx-auto mb-4 ${isDarkMode ? "border-cyan-500" : "border-black"
                        }`} />
                    <p className={`font-black uppercase tracking-widest text-[10px] ${isDarkMode ? "text-zinc-500" : "text-zinc-400"}`}>
                        Initializing Architecture...
                    </p>
                </div>
            </div>
        );
    }

    if (error || !course) {
        return (
            <div className={`min-h-screen flex items-center justify-center ${isDarkMode ? "bg-black" : "bg-white"}`}>
                <div className="text-center px-6">
                    <h2 className="text-4xl font-black font-syne uppercase tracking-tighter mb-4 text-red-500">Node Failure.</h2>
                    <p className={`mb-8 font-medium ${isDarkMode ? "text-zinc-400" : "text-zinc-600"}`}>{error || "Record not found in the grid."}</p>
                    <Link to="/courses" className={`px-8 py-4 rounded-xl font-black uppercase tracking-widest text-sm transition-all ${isDarkMode ? "bg-zinc-800 text-white hover:bg-zinc-700" : "bg-black text-white hover:bg-zinc-800"
                        }`}>
                        Return to Core
                    </Link>
                </div>
            </div>
        );
    }

    const statuses = getCourseStatuses(course);
    const gst = Math.round(course.discountedPrice * 0.18);
    const totalPrice = course.discountedPrice + gst;

    return (
        <div className={`min-h-screen pb-24 transition-colors duration-500 ${isDarkMode ? "bg-black text-white" : "bg-white text-black"
            }`}>
            {/* Dynamic Hero Section */}
            <section className="relative pt-40 pb-20 px-6 overflow-hidden">
                {/* Background Gradients */}
                <div className="absolute inset-0 pointer-events-none -z-10">
                    <div className={`absolute top-0 right-[-10%] w-[60%] h-[60%] rounded-full blur-[180px] opacity-20 ${isDarkMode ? "bg-cyan-500/30" : "bg-blue-200/50"
                        }`} />
                    <div className={`absolute bottom-[-10%] left-[-10%] w-[60%] h-[60%] rounded-full blur-[180px] opacity-20 ${isDarkMode ? "bg-fuchsia-500/20" : "bg-rose-200/50"
                        }`} />
                </div>

                <div className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-12 gap-12 items-center">
                    <div className="lg:col-span-8">
                        {/* Badges Container */}
                        <div className="flex flex-wrap gap-3 mb-8">
                            {statuses.map(status => (
                                <span key={status} className={`px-4 py-1.5 rounded-full text-[10px] font-black uppercase tracking-[0.2em] border ${isDarkMode ? "bg-zinc-900/50 border-cyan-500/20 text-cyan-400" : "bg-zinc-100 border-zinc-200 text-zinc-600"
                                    }`}>
                                    {status}
                                </span>
                            ))}
                            <span className={`px-4 py-1.5 rounded-full text-[10px] font-black uppercase tracking-[0.2em] border ${isDarkMode ? "bg-zinc-900/50 border-fuchsia-500/20 text-fuchsia-400" : "bg-rose-50 border-rose-100 text-rose-600"
                                }`}>
                                {course.language}
                            </span>
                        </div>

                        {/* Course Title */}
                        <h1 className="text-5xl md:text-8xl font-black font-syne tracking-tighter uppercase leading-[0.8] mb-8">
                            {course.name.split(' ').map((word, i) => (
                                <span key={i} className={i % 2 !== 0 ? "text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 via-blue-500 to-fuchsia-500" : ""}>
                                    {word}{' '}
                                </span>
                            ))}
                        </h1>

                        <p className={`text-xl md:text-2xl font-medium max-w-2xl leading-relaxed mb-10 ${isDarkMode ? "text-zinc-400" : "text-zinc-600"
                            }`}>
                            {course.description}
                        </p>

                        {/* Action Group */}
                        <div className="flex flex-wrap items-center gap-6">
                            <Link
                                to={`/checkout/${course.id}`}
                                className={`flex items-center gap-3 px-10 py-5 rounded-2xl font-black text-lg uppercase tracking-widest transition-all scale-100 hover:scale-[1.03] active:scale-95 shadow-2xl ${isDarkMode
                                        ? "bg-cyan-500 text-white shadow-cyan-900/40 hover:bg-cyan-400"
                                        : "bg-black text-white shadow-black/30 hover:bg-zinc-800"
                                    }`}
                            >
                                Enroll Now <ChevronRight size={20} />
                            </Link>

                            <div className="flex flex-col">
                                <span className={`text-[10px] font-black uppercase tracking-widest mb-1 ${isDarkMode ? "text-zinc-500" : "text-zinc-400"}`}>
                                    Investment Term
                                </span>
                                <span className="text-2xl font-bold font-syne">
                                    ₹{course.discountedPrice.toLocaleString('en-IN')}
                                </span>
                            </div>
                        </div>
                    </div>

                    {/* Feature Badge Grid */}
                    <div className="lg:col-span-4 hidden lg:block">
                        <div className={`p-8 rounded-[3rem] border transition-all ${isDarkMode ? "bg-zinc-900/30 border-zinc-800" : "bg-zinc-50 border-zinc-100 shadow-sm"
                            }`}>
                            <div className="space-y-8">
                                {course.technicalSpecs?.map((spec, idx) => (
                                    <div key={idx} className="flex items-center gap-5 group">
                                        <div className={`w-14 h-14 rounded-2xl flex items-center justify-center border transition-all duration-500 ${isDarkMode ? "bg-zinc-950 border-zinc-800 text-cyan-500 group-hover:border-cyan-500/50" : "bg-white border-zinc-200 text-zinc-900 shadow-xs"
                                            }`}>
                                            {/* Using a generic award/barchart if icon not found, or ideally a mapping */}
                                            {idx % 2 === 0 ? <Award size={24} /> : <BarChart size={24} />}
                                        </div>
                                        <div>
                                            <h4 className={`text-[10px] font-black uppercase tracking-widest mb-1 ${isDarkMode ? "text-zinc-500" : "text-zinc-400"}`}>
                                                {spec.label}
                                            </h4>
                                            <p className="text-lg font-black font-syne leading-none uppercase">{spec.value}</p>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </section>

            {/* Main Content Area */}
            <section className="max-w-7xl mx-auto px-6 grid grid-cols-1 lg:grid-cols-12 gap-20 py-20 border-t border-zinc-500/10">

                {/* Left Column: Syllabus System */}
                <div className="lg:col-span-8">
                    <div className="flex items-center gap-4 mb-12">
                        <div className="w-12 h-px bg-cyan-500" />
                        <h2 className="text-3xl md:text-5xl font-black font-syne uppercase tracking-tighter">Strategic Syllabus.</h2>
                    </div>

                    <div className="space-y-4">
                        {course.syllabusPhases?.map((phase, pIdx) => (
                            <div key={pIdx} className={`rounded-[2rem] border transition-all duration-500 overflow-hidden ${openPhaseIndex === pIdx
                                    ? (isDarkMode ? "bg-zinc-900/40 border-cyan-500/30" : "bg-zinc-50 border-blue-500/30 shadow-md")
                                    : (isDarkMode ? "bg-transparent border-zinc-800 hover:border-zinc-700" : "bg-white border-zinc-100 hover:border-zinc-200")
                                }`}>
                                <button
                                    onClick={() => setOpenPhaseIndex(openPhaseIndex === pIdx ? -1 : pIdx)}
                                    className="w-full px-8 py-8 flex items-center justify-between group"
                                >
                                    <div className="flex items-center gap-8 text-left">
                                        <span className={`text-4xl font-black font-syne opacity-20 group-hover:opacity-100 transition-opacity ${openPhaseIndex === pIdx ? "opacity-100 text-cyan-500" : ""
                                            }`}>
                                            {String(pIdx + 1).padStart(2, '0')}
                                        </span>
                                        <div>
                                            <span className={`text-[10px] font-black uppercase tracking-[0.3em] mb-2 block ${isDarkMode ? "text-zinc-500" : "text-zinc-400"
                                                }`}>
                                                {phase.month}
                                            </span>
                                            <h3 className="text-xl md:text-2xl font-black font-syne uppercase tracking-tight leading-none group-hover:text-cyan-400 transition-colors">
                                                {phase.title}
                                            </h3>
                                        </div>
                                    </div>
                                    <div className={`w-10 h-10 rounded-full flex items-center justify-center border transition-all ${openPhaseIndex === pIdx
                                            ? "bg-cyan-500 border-cyan-500 text-white rotate-180"
                                            : (isDarkMode ? "border-zinc-800 text-zinc-500" : "border-zinc-200 text-zinc-400")
                                        }`}>
                                        <ChevronDown size={20} />
                                    </div>
                                </button>

                                <AnimatePresence>
                                    {openPhaseIndex === pIdx && (
                                        <motion.div
                                            initial={{ height: 0, opacity: 0 }}
                                            animate={{ height: "auto", opacity: 1 }}
                                            exit={{ height: 0, opacity: 0 }}
                                            transition={{ duration: 0.4, ease: [0.16, 1, 0.3, 1] }}
                                        >
                                            <div className="px-8 pb-10 ml-0 md:ml-16">
                                                <p className={`text-lg font-medium leading-relaxed mb-8 max-w-2xl ${isDarkMode ? "text-zinc-400" : "text-zinc-600"
                                                    }`}>
                                                    {phase.desc}
                                                </p>

                                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                                    {phase.weeks?.map((week, wIdx) => (
                                                        <div key={wIdx} className={`p-6 rounded-2xl border transition-colors ${isDarkMode ? "bg-zinc-950/50 border-zinc-800" : "bg-white border-zinc-200 shadow-xs"
                                                            }`}>
                                                            <h4 className={`text-[10px] font-black uppercase tracking-widest mb-4 flex items-center gap-2 ${isDarkMode ? "text-fuchsia-400" : "text-rose-500"
                                                                }`}>
                                                                <div className={`w-1.5 h-1.5 rounded-full ${isDarkMode ? "bg-fuchsia-400" : "bg-rose-500"}`} />
                                                                {week.label}: {week.title}
                                                            </h4>
                                                            <ul className="space-y-3">
                                                                {week.topics?.map((topic, tIdx) => (
                                                                    <li key={tIdx} className={`flex items-start gap-3 text-sm font-bold ${isDarkMode ? "text-zinc-300" : "text-zinc-700"
                                                                        }`}>
                                                                        <CheckCircle2 size={16} className="shrink-0 text-cyan-500 mt-0.5" />
                                                                        {topic.name}
                                                                    </li>
                                                                ))}
                                                            </ul>
                                                        </div>
                                                    ))}
                                                </div>
                                            </div>
                                        </motion.div>
                                    )}
                                </AnimatePresence>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Right Column: Sticky Pricing Card */}
                <div className="lg:col-span-4">
                    <div className="sticky top-32 space-y-8">
                        <div className={`p-8 rounded-[3rem] border transition-all ${isDarkMode
                                ? "bg-zinc-900 border-zinc-800 shadow-[0_30px_60px_-15px_rgba(0,0,0,0.5)]"
                                : "bg-white border-zinc-100 shadow-[0_30px_60px_-15px_rgba(0,0,0,0.1)]"
                            }`}>
                            <h4 className={`text-xs font-black uppercase tracking-[0.3em] mb-10 text-center ${isDarkMode ? "text-zinc-500" : "text-zinc-400"}`}>
                                Deployment Access
                            </h4>

                            <div className="space-y-6 mb-10">
                                <div className="flex justify-between items-center px-2">
                                    <span className={`text-sm font-bold ${isDarkMode ? "text-zinc-500" : "text-zinc-400"}`}>Base Curriculum Fee</span>
                                    <span className="text-xl font-bold font-syne line-through opacity-30">₹{course.originalPrice.toLocaleString('en-IN')}</span>
