export default CourseDetail;

import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Search, Filter, BookOpen } from "lucide-react";
import { useTheme } from "../context";
import coursesApi, { type Course, type CourseStatus } from "../../services/api";
import CourseCard from "../components/CourseCard";

const Courses: React.FC = () => {
    const { isDarkMode } = useTheme();
    const [courses, setCourses] = useState<Course[]>([]);
    const [filteredCourses, setFilteredCourses] = useState<Course[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [searchQuery, setSearchQuery] = useState("");
    const [activeFilter, setActiveFilter] = useState<CourseStatus | "All">("All");

    useEffect(() => {
        const fetchCourses = async () => {
            try {
                setLoading(true);
                const data = await coursesApi.getAllCourses();
                setCourses(data);
                setFilteredCourses(data);
            } catch (err) {
                setError(err instanceof Error ? err.message : "Failed to load courses");
            } finally {
                setLoading(false);
            }
        };
        fetchCourses();
    }, []);

    useEffect(() => {
        let result = courses;

        if (activeFilter !== "All") {
            result = result.filter((course) => {
                if (Array.isArray(course.status)) {
                    return course.status.includes(activeFilter as CourseStatus);
                }
                return course.status === activeFilter;
            });
        }

        if (searchQuery) {
            const query = searchQuery.toLowerCase();
            result = result.filter(
                (course) =>
                    course.name.toLowerCase().includes(query) ||
                    course.description.toLowerCase().includes(query)
            );
        }

        setFilteredCourses(result);
    }, [searchQuery, activeFilter, courses]);

    const filterOptions: (CourseStatus | "All")[] = ["All", "Online", "Offline", "Hybrid"];

    return (
        <div className={`min-h-screen pt-32 pb-24 transition-colors duration-500 ${isDarkMode ? "bg-black text-white" : "bg-white text-black"
            }`}>
            {/* Background Gradients */}
            <div className="fixed inset-0 pointer-events-none overflow-hidden -z-10">
                <div className={`absolute top-0 right-0 w-[500px] h-[500px] rounded-full blur-[150px] opacity-10 ${isDarkMode ? "bg-cyan-500" : "bg-blue-200"
                    }`} />
                <div className={`absolute bottom-0 left-0 w-[500px] h-[500px] rounded-full blur-[150px] opacity-10 ${isDarkMode ? "bg-fuchsia-500" : "bg-rose-200"
                    }`} />
            </div>

            <div className="max-w-7xl mx-auto px-6">
                {/* Header Section */}
                <div className="mb-16">
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        className="flex items-center gap-3 mb-4"
                    >
                        <span className={`px-4 py-1.5 rounded-full text-[10px] font-black uppercase tracking-widest border ${isDarkMode ? "bg-zinc-900 border-zinc-800 text-cyan-400" : "bg-zinc-100 border-zinc-200 text-zinc-500"
                            }`}>
                            Vanguard Curriculum
                        </span>
                    </motion.div>
                    <motion.h1
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.1 }}
                        className="text-5xl md:text-8xl font-black font-syne tracking-tighter uppercase leading-[0.8] mb-8"
                    >
                        Master the <br />
                        <span className="text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 via-blue-500 to-fuchsia-500">
                            Future Skills.
                        </span>
                    </motion.h1>
                    <motion.p
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.2 }}
                        className={`text-xl md:text-2xl font-medium max-w-2xl leading-relaxed ${isDarkMode ? "text-zinc-400" : "text-zinc-600"
                            }`}
                    >
                        Our vocational-first approach ensures you aren't just learning, but building industry-standard artifacts throughout your journey.
                    </motion.p>
                </div>

                {/* Interactive Controls Section */}
                <div className="mb-16 flex flex-col xl:flex-row gap-8 items-stretch xl:items-center">
                    {/* Search Bar */}
                    <div className="relative flex-1 group">
                        <Search className={`absolute left-6 top-1/2 -translate-y-1/2 transition-colors duration-300 ${isDarkMode ? "text-zinc-500 group-focus-within:text-cyan-400" : "text-zinc-400 group-focus-within:text-black"
                            }`} size={22} />
                        <input
                            type="text"
                            placeholder="Identify Curriculum Node..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            className={`w-full py-5 pl-16 pr-8 rounded-3xl border-2 outline-none transition-all font-bold text-base ${isDarkMode
                                ? "bg-zinc-900/50 border-zinc-800 focus:border-cyan-500/50 text-white placeholder-zinc-700 shadow-2xl"
                                : "bg-zinc-50 border-zinc-100 focus:border-black placeholder-zinc-300 shadow-xl"
                                }`}
                        />
                    </div>

                    {/* Type Filters */}
                    <div className={`p-2 rounded-3xl border flex gap-1 items-center overflow-x-auto no-scrollbar ${isDarkMode ? "bg-zinc-900/50 border-zinc-800" : "bg-zinc-50 border-zinc-200"
                        }`}>
                        {filterOptions.map((option) => (
                            <button
                                key={option}
                                onClick={() => setActiveFilter(option)}
                                className={`px-8 py-3 rounded-2xl text-[10px] font-black uppercase tracking-[0.2em] whitespace-nowrap transition-all duration-300 ${activeFilter === option
                                    ? isDarkMode
                                        ? "bg-cyan-500 text-white shadow-lg shadow-cyan-900/20"
                                        : "bg-black text-white shadow-lg shadow-black/20"
                                    : isDarkMode
                                        ? "text-zinc-500 hover:text-white"
                                        : "text-zinc-400 hover:text-black"
                                    }`}
                            >
                                {option}
                            </button>
                        ))}
                    </div>
                </div>

                {/* Content Section */}
                <AnimatePresence mode="wait">
                    {loading ? (
                        <motion.div
                            key="loading"
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            exit={{ opacity: 0 }}
                            className="py-24 text-center"
                        >
                            <div className={`w-16 h-16 border-4 border-t-transparent animate-spin rounded-full mx-auto mb-6 ${isDarkMode ? "border-cyan-500" : "border-black"
                                }`} />
                            <p className="font-bold text-zinc-500 uppercase tracking-widest text-sm">Synchronizing Curriculum...</p>
                        </motion.div>
                    ) : error ? (
                        <motion.div
                            key="error"
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            className="py-24 text-center border-2 border-red-500/20 rounded-[3rem] bg-red-500/5"
                        >
                            <p className="text-red-500 font-bold mb-4">{error}</p>
                            <button
                                onClick={() => window.location.reload()}
                                className="px-8 py-3 bg-red-500 text-white rounded-xl font-black uppercase tracking-widest hover:bg-red-600 transition-colors"
                            >
                                Retry System
                            </button>
                        </motion.div>
                    ) : (
                        <motion.div
                            key="grid"
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
                        >
                            {filteredCourses.length > 0 ? (
                                filteredCourses.map((course) => (
                                    <CourseCard key={course._id} course={course} />
                                ))
                            ) : (
                                <div className="col-span-full py-32 text-center">
                                    <BookOpen className="mx-auto mb-6 text-zinc-500 opacity-20" size={80} />
                                    <h3 className="text-2xl font-bold mb-2 uppercase tracking-tighter">No Courses Identified</h3>
                                    <p className="text-zinc-500 font-medium">Try refining your search or filter parameters.</p>
                                </div>
                            )}
                        </motion.div>
                    )}
                </AnimatePresence>
            </div>
        </div>
    );
};

export default Courses;

import React, { useState, useEffect } from 'react';
import { useAuth } from '../../AuthContext';
import { useTheme, useData } from '../../context';
import { motion } from 'framer-motion';
import axios from 'axios';
import {
