} from 'lucide-react';
import { Routes, Route, Link, useLocation, useNavigate } from 'react-router-dom';
import { useNotification } from '../../NotificationContext';
import { getCourseStatuses } from '../../services/api';

// Sidebar Item Component
const SidebarItem = ({ icon: Icon, label, path, active, onClick }: any) => (
    <Link
        to={path}
        onClick={onClick}
        className={`flex items-center gap-4 px-6 py-4 transition-all duration-300 relative group ${active
            ? 'text-cyan-400 bg-cyan-400/5'
            : 'text-zinc-500 hover:text-zinc-200 hover:bg-white/5'
            }`}
    >
        {active && (
            <motion.div
                layoutId="sidebar-active"
                className="absolute left-0 w-1 h-8 bg-cyan-500 rounded-full"
            />
        )}
        <Icon size={20} className={active ? 'text-cyan-400' : 'group-hover:text-zinc-200'} />
        <span className="font-bold text-[10px]">{label}</span>
    </Link>
);

const sidebarItems = [
    { icon: LayoutDashboard, label: 'Overview', path: '/lms' },
    { icon: BookOpen, label: 'My Courses', path: '/lms/courses' },
    { icon: Compass, label: 'Browse Tracks', path: '/lms/browse' },
    { icon: Award, label: 'Certificates', path: '/lms/certificates' },
    { icon: Settings, label: 'Settings', path: '/lms/settings' },
];

const Dashboard: React.FC = () => {
