                </div>
            </aside>

            {/* Main Content */}
            <main className="flex-grow md:ml-72 flex flex-col min-h-screen relative overflow-hidden pt-8 md:pt-0">
                {/* Background Glow */}
                <div className={`absolute top-0 right-0 w-[600px] h-[600px] blur-[120px] rounded-full opacity-10 pointer-events-none ${isDarkMode ? 'bg-cyan-500' : 'bg-cyan-200'}`}></div>

                {/* Top Header */}
                <header className={`h-20 border-b flex items-center justify-between px-8 relative z-10 ${isDarkMode ? 'border-white/5 bg-zinc-950/50' : 'border-black/5 bg-white/50'} backdrop-blur-xl`}>
                    <div className="flex items-center gap-4 flex-grow max-w-xl">
                        <div className={`flex items-center gap-3 px-4 py-2 rounded-2xl w-full border ${isDarkMode ? 'bg-zinc-900/50 border-white/5' : 'bg-white border-black/5'}`}>
                            <Search size={18} className="text-zinc-500" />
