                        <div className="flex items-center gap-4 pl-6 border-l border-white/5">
                            <div className="text-right hidden sm:block">
                                <p className="font-bold text-[10px]">{user?.name || 'Student'}</p>
                                <p className="text-[8px] font-bold text-zinc-500">{user?.role || 'Learner'}</p>
                            </div>
                            <div className="h-10 w-10 rounded-2xl bg-gradient-to-br from-cyan-400 to-blue-600 p-[2px]">
