                        <Route path="/settings" element={<ProfileSettings />} />
                    </Routes>
                </div>
            </main>
        </div>
    );
};

// --- Sub-components (could be in separate files) ---

const Overview = () => {
    const { user } = useAuth();
    const { isDarkMode } = useTheme();
    const { courses } = useData();
    const [myCourses, setMyCourses] = useState<any[]>([]);

    useEffect(() => {
        const fetchMyCourses = async () => {
            try {
                const baseUrl = import.meta.env.VITE_API_BASE_URL || 'http://localhost:5000';
                const res = await axios.get(`${baseUrl}/api/user/my-courses`, {
                    headers: { Authorization: `Bearer ${user?.token}` }
                });
                if (res.data.success) {
                    setMyCourses(res.data.data);
                }
            } catch (err) {
                console.error("Failed to fetch my courses", err);
            }
        };
        if (user?.token) fetchMyCourses();
    }, [user]);

    // Recommended course (first one from database that isn't purchased, or just first one)
    const recommendedCourse = courses.find(c => !myCourses.find(mc => mc._id === c._id)) || courses[0];

    return (
        <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="space-y-8"
        >
            <div className="flex flex-col gap-2">
                <h1 className="font-bold text-4xl">Welcome back, {user?.name?.split(' ')[0]}</h1>
                <p className="text-zinc-500 font-bold text-[10px]">Your learning progression is at peak performance.</p>
            </div>

            {/* Stats Grid */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {[
                    { label: 'Courses Active', value: myCourses.length.toString().padStart(2, '0'), progress: myCourses.length > 0 ? 65 : 0, color: 'from-cyan-400 to-blue-600' },
                    { label: 'Hours Studied', value: '128', progress: 80, color: 'from-fuchsia-500 to-purple-600' },
                    { label: 'Certificates Earned', value: '01', progress: 20, color: 'from-emerald-400 to-teal-600' }
                ].map((stat, i) => (
                    <div key={i} className={`p-8 rounded-[2rem] border refractive-border liquid-glass ${isDarkMode ? 'bg-zinc-900/50 border-white/5' : 'bg-white border-black/5'}`}>
                        <p className="text-zinc-500 font-bold text-[10px] mb-2">{stat.label}</p>
                        <h3 className="font-bold text-4xl mb-6">{stat.value}</h3>
                        <div className="h-1.5 w-full bg-zinc-800 rounded-full overflow-hidden">
                            <motion.div
                                initial={{ width: 0 }}
                                animate={{ width: `${stat.progress}%` }}
                                className={`h-full bg-gradient-to-r ${stat.color}`}
                            />
                        </div>
                    </div>
                ))}
            </div>

            {/* Recent Activity & Next Lesson */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                <div className={`p-8 rounded-[2.5rem] border ${isDarkMode ? 'bg-zinc-900/30 border-white/5' : 'bg-white border-black/5'}`}>
                    <h3 className="font-bold text-xl mb-6">Recent Engagement</h3>
                    <div className="space-y-6">
                        {myCourses.length > 0 ? (
                            myCourses.slice(0, 3).map((course) => (
                                <div key={course._id} className="flex items-center gap-4 group cursor-pointer">
                                    <div className="h-12 w-12 rounded-2xl bg-zinc-800/50 flex items-center justify-center shrink-0">
                                        <BookOpen size={20} className="text-zinc-500 group-hover:text-cyan-400" />
                                    </div>
                                    <div className="flex-grow">
                                        <p className="font-bold text-[10px] group-hover:text-cyan-400 transition-colors">{course.name}</p>
                                        <p className="text-[8px] font-bold text-zinc-600">Active Track • {course.status}</p>
                                    </div>
                                    <ChevronRight size={16} className="text-zinc-700 group-hover:text-white" />
                                </div>
                            ))
                        ) : (
                            <p className="text-zinc-500 font-bold uppercase tracking-widest text-[10px] py-4">No active courses. Start your first track today.</p>
                        )}
                    </div>
                </div>

                {recommendedCourse && (
                    <div className={`p-8 rounded-[2.5rem] transition-all relative overflow-hidden group border ${isDarkMode ? 'bg-zinc-900 border-white/5' : 'bg-white border-black/5'}`}>
                        {/* Background Image with Overlay */}
                        <div className="absolute inset-0 opacity-20 group-hover:opacity-30 transition-opacity duration-700">
                            <img
                                src={recommendedCourse.certificateImage || 'https://images.unsplash.com/photo-1635070041078-e363dbe005cb?q=80&w=2070&auto=format&fit=crop'}
                                className="w-full h-full object-cover grayscale"
                                alt=""
                            />
                            <div className={`absolute inset-0 bg-gradient-to-br ${isDarkMode ? 'from-zinc-950 via-zinc-950/80 to-transparent' : 'from-white via-white/80 to-transparent'}`}></div>
                        </div>

                        <div className="relative z-10">
                            <span className="px-3 py-1 rounded-full bg-cyan-500 text-white font-bold text-[8px] shadow-lg shadow-cyan-500/20">Recommended Next</span>
                            <h3 className="font-bold text-3xl mt-4 mb-2 group-hover:text-cyan-400 transition-colors">{recommendedCourse.name}</h3>
                            <p className="text-[10px] font-bold text-zinc-500 max-w-xs mb-8">{recommendedCourse.description?.substring(0, 100)}...</p>
                            <Link
                                to={`/course/${recommendedCourse.id}`}
                                className="inline-block px-10 py-5 rounded-2xl bg-cyan-500 text-white font-bold text-[10px] hover:bg-cyan-400 transition-all transform hover:scale-105 active:scale-95 shadow-xl shadow-cyan-500/20"
                            >
                                View Details
                            </Link>
                        </div>
                        <BookOpen size={140} className="absolute -bottom-10 -right-10 opacity-5 text-cyan-500 -rotate-12 group-hover:rotate-0 transition-transform duration-700" />
                    </div>
                )}
            </div>
        </motion.div>
    );
};

const MyCourses = () => {
    const [myCourses, setMyCourses] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [selectionMode, setSelectionMode] = useState(false);
    const [selectedIds, setSelectedIds] = useState<string[]>([]);
    const [deleting, setDeleting] = useState(false);
    const { user } = useAuth();
    const { isDarkMode } = useTheme();
    const { showToast, confirm } = useNotification();

    const fetchMyCourses = async () => {
        try {
            const baseUrl = import.meta.env.VITE_API_BASE_URL || 'http://localhost:5000';
            const res = await axios.get(`${baseUrl}/api/user/my-courses`, {
                headers: { Authorization: `Bearer ${user?.token}` }
            });
            if (res.data.success) {
                setMyCourses(res.data.data);
            }
        } catch (err) {
            console.error("Failed to fetch courses", err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (user) fetchMyCourses();
    }, [user]);

    const toggleSelection = (id: string) => {
        if (selectedIds.includes(id)) {
            setSelectedIds(selectedIds.filter(i => i !== id));
        } else {
            setSelectedIds([...selectedIds, id]);
        }
    };

    const handleDelete = async () => {
        if (selectedIds.length === 0) return;

        const confirmed = await confirm(
            "Final Warning",
            `You are about to permanently purge ${selectedIds.length} tracks from your learning archive. This action cannot be undone.`
        );

        if (!confirmed) return;

        setDeleting(true);
        try {
            const baseUrl = import.meta.env.VITE_API_BASE_URL || 'http://localhost:5000';
            await axios.delete(`${baseUrl}/api/user/remove-courses`, {
                data: { courseIds: selectedIds },
                headers: { Authorization: `Bearer ${user?.token}` }
            });
            await fetchMyCourses();
            showToast(`${selectedIds.length} tracks removed from archive`, 'success');
            setSelectionMode(false);
            setSelectedIds([]);
        } catch (err) {
            showToast("Failed to purge tracks", 'error');
        } finally {
            setDeleting(false);
        }
    };

    if (loading) return <div className="p-8 text-center text-[10px] font-bold uppercase tracking-[0.3em]">Synching library...</div>;

    return (
        <div className="space-y-8">
            <div className="flex justify-between items-center">
                <div className="flex items-center gap-4">
                    <div>
                        <h2 className="font-bold text-3xl">My Archive</h2>
                        <p className="text-zinc-500 font-bold text-[10px]">Your verified knowledge path.</p>
                    </div>
                    {myCourses.length > 0 && !selectionMode && (
                        <button
                            onClick={() => setSelectionMode(true)}
                            className="p-3 rounded-full hover:bg-red-500/10 text-zinc-500 hover:text-red-500 transition-all"
                        >
                            <Trash2 size={20} />
                        </button>
                    )}
                </div>

                {selectionMode && (
                    <div className="flex items-center gap-4 animate-in fade-in slide-in-from-right-4">
                        <span className="text-[10px] font-bold text-zinc-500 uppercase tracking-widest">{selectedIds.length} Selected</span>
                        <button
                            onClick={() => { setSelectionMode(false); setSelectedIds([]); }}
                            className="px-6 py-2 rounded-xl text-[10px] font-bold uppercase tracking-widest text-zinc-400 hover:text-white transition-colors"
                        >
                            Cancel
                        </button>
                        <button
                            onClick={handleDelete}
                            disabled={deleting || selectedIds.length === 0}
                            className="px-6 py-3 rounded-xl bg-red-500 text-white text-[10px] font-bold uppercase tracking-widest hover:bg-red-600 transition-all disabled:opacity-50"
                        >
                            {deleting ? 'Purging...' : 'Delete Permanently'}
                        </button>
                    </div>
                )}
            </div>

            {myCourses.length === 0 ? (
                <div className="h-[40vh] flex items-center justify-center flex-col gap-4 border-2 border-dashed border-white/5 rounded-[3rem]">
                    <BookOpen size={40} className="text-zinc-800" />
                    <p className="text-zinc-600 font-bold text-[10px]">Your shelf is empty</p>
                    <Link to="/lms/browse" className="px-6 py-3 rounded-xl bg-white text-black font-bold text-[9px]">Enroll Now</Link>
                </div>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {myCourses.map((course) => {
                        const isSelected = selectedIds.includes(course._id);
                        return (
                            <div
                                key={course._id}
                                onClick={() => selectionMode ? toggleSelection(course._id) : null}
                                className={`p-6 rounded-[2.5rem] border relative ${isDarkMode ? 'bg-zinc-900/50 border-white/5' : 'bg-white border-black/5'} group transition-all duration-300 ${selectionMode ? 'cursor-pointer' : 'cursor-default'} ${isSelected ? 'ring-2 ring-red-500 border-red-500/20' : ''}`}
                            >
                                {selectionMode && (
                                    <div className={`absolute top-4 right-4 h-6 w-6 rounded-lg border-2 flex items-center justify-center transition-all z-20 ${isSelected ? 'bg-red-500 border-red-500' : 'border-white/20 bg-black/40'}`}>
                                        {isSelected && <Check size={14} className="text-white" />}
                                    </div>
                                )}
                                <div className="aspect-video rounded-2xl bg-zinc-800 mb-6 overflow-hidden relative">
                                    <img src={course.certificateImage || 'https://images.unsplash.com/photo-1620641788421-7a1c342ea42e?q=80&w=1974&auto=format&fit=crop'} className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-700" alt="" />
                                    <div className="absolute top-4 left-4 px-3 py-1 bg-cyan-500 text-white font-bold text-[8px] rounded-full">{course.status}</div>
                                </div>
                                <h4 className="font-bold text-lg group-hover:text-cyan-400 transition-colors">{course.name}</h4>
                                <div className="flex items-center justify-between text-[8px] font-bold text-zinc-500">
                                    <span>{course.language}</span>
                                    {!selectionMode && <span className="text-cyan-500">Resume Lesson</span>}
                                </div>
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
    );
};

const BrowseCourses = () => {
    const { courses } = useData();
    const { user } = useAuth();
    const { isDarkMode } = useTheme();
    const navigate = useNavigate();
    const [myCourses, setMyCourses] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchMyCourses = async () => {
            try {
                const baseUrl = import.meta.env.VITE_API_BASE_URL || 'http://localhost:5000';
                const res = await axios.get(`${baseUrl}/api/user/my-courses`, {
                    headers: { Authorization: `Bearer ${user?.token}` }
                });
                if (res.data.success) {
                    setMyCourses(res.data.data);
                }
            } catch (err) {
                console.error("Failed to fetch ownership status", err);
            } finally {
                setLoading(false);
            }
        };
        if (user?.token) fetchMyCourses();
    }, [user]);

    const isPurchased = (courseId: string) => {
        return myCourses.some(c => c.id === courseId || c._id === courseId);
    };

    if (loading) return <div className="p-12 text-center text-[10px] font-bold uppercase tracking-widest text-zinc-500">Authenticating Track Access...</div>;

    return (
        <div className="space-y-8">
            <div className="flex flex-col gap-2">
                <h2 className="font-bold text-3xl">Available Tracks</h2>
                <p className="text-zinc-500 font-bold text-[10px]">Initialize your next professional evolution.</p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {courses.map((course) => {
                    const owned = isPurchased(course.id);
                    const statuses = getCourseStatuses(course);
                    const discountPercent = course.originalPrice > course.discountedPrice
                        ? Math.round(((course.originalPrice - course.discountedPrice) / course.originalPrice) * 100)
                        : 0;
                    const gst = Math.round(course.discountedPrice * 0.18);
                    const totalPrice = course.discountedPrice + gst;
                    const isHighDiscount = discountPercent >= 40;

                    return (
                        <div key={course.id} className={`flex flex-col rounded-[2.5rem] border transition-all duration-500 group overflow-hidden ${isDarkMode ? 'bg-zinc-900/30 border-zinc-800 hover:border-cyan-500/50 hover:shadow-cyan-950/20' : 'bg-white border-zinc-100 hover:border-black hover:shadow-black/10'}`}>
                            {/* Image Section */}
                            <div className="relative h-60 m-3 rounded-[1.8rem] overflow-hidden">
                                <img
                                    src={course.certificateImage || 'https://images.unsplash.com/photo-1635070041078-e363dbe005cb?q=80&w=2070&auto=format&fit=crop'}
                                    className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-1000"
                                    alt={course.name}
                                />

                                <div className="absolute inset-0 bg-linear-to-t from-black/80 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>

                                {/* Top Badges */}
                                <div className="absolute top-4 left-4 flex flex-wrap gap-2">
                                    {owned && (
                                        <span className="px-3 py-1.5 bg-emerald-500 text-white font-black text-[10px] uppercase tracking-widest rounded-full shadow-lg">Owned</span>
                                    )}
                                    {!owned && isHighDiscount && (
                                        <span className={`text-white text-[10px] font-black px-3 py-1.5 rounded-full uppercase tracking-widest shadow-lg ${isDarkMode ? "bg-cyan-600" : "bg-emerald-500"}`}>
                                            Best Value
                                        </span>
                                    )}
                                    {!owned && discountPercent > 0 && (
                                        <span className={`text-white text-[10px] font-black px-3 py-1.5 rounded-full uppercase tracking-widest shadow-lg ${isDarkMode ? "bg-fuchsia-600" : "bg-rose-500"}`}>
                                            {discountPercent}% OFF
                                        </span>
                                    )}
                                </div>

                                {/* Bottom Info - Status(es) and Language */}
                                <div className="absolute bottom-4 left-4 right-4 flex items-center justify-between opacity-0 group-hover:opacity-100 transform translate-y-4 group-hover:translate-y-0 transition-all duration-500">
                                    <div className="flex flex-wrap gap-2">
                                        {statuses.map((status: string) => (
                                            <span key={status} className={`px-3 py-1.5 text-white text-[10px] font-black rounded-full uppercase tracking-widest ${status === "Online" ? (isDarkMode ? "bg-cyan-700" : "bg-blue-600") :
                                                    status === "Offline" ? (isDarkMode ? "bg-fuchsia-700" : "bg-rose-600") :
                                                        (isDarkMode ? "bg-purple-700" : "bg-purple-600")
                                                }`}>
                                                {status}
                                            </span>
                                        ))}
                                    </div>
                                    <span className="px-3 py-1.5 bg-black/50 backdrop-blur-md text-white text-[10px] font-black rounded-full uppercase tracking-widest">
                                        {course.language}
                                    </span>
                                </div>
                            </div>

                            <div className="px-8 pb-8 pt-4 flex flex-col grow">
                                <h4 className={`text-2xl font-bold font-syne leading-tight tracking-tight group-hover:text-cyan-400 transition-colors ${isDarkMode ? "text-zinc-100" : "text-zinc-900"}`}>{course.name}</h4>
                                <p className={`text-sm mb-6 mt-2 line-clamp-2 ${isDarkMode ? "text-zinc-400" : "text-zinc-600"}`}>{course.description}</p>

                                <div className="mt-auto space-y-6">
                                    <div className={`p-5 rounded-2xl border transition-colors ${isDarkMode ? "bg-zinc-950/80 border-zinc-800 group-hover:border-cyan-500/20" : "bg-zinc-50 border-zinc-100 group-hover:border-blue-500/30"}`}>
                                        <div className="flex items-end justify-between mb-3">
                                            <div>
                                                <p className="text-[10px] font-black text-zinc-500 uppercase tracking-widest mb-1">Course Fee</p>
                                                <div className="flex items-center gap-2">
                                                    <span className={`text-3xl font-black ${isDarkMode ? "text-white" : "text-zinc-900"}`}>₹{course.discountedPrice.toLocaleString("en-IN")}</span>
                                                    {course.originalPrice !== course.discountedPrice && (
                                                        <span className={`text-sm line-through decoration-2 ${isDarkMode ? "text-zinc-600 decoration-fuchsia-500/50" : "text-zinc-400 decoration-rose-500/50"}`}>
                                                            ₹{course.originalPrice.toLocaleString("en-IN")}
                                                        </span>
                                                    )}
                                                </div>
                                            </div>
                                            {!owned && discountPercent > 0 && (
                                                <div className={`text-white text-[10px] font-black px-2 py-1 rounded-md mb-1 ${isDarkMode ? "bg-cyan-600" : "bg-emerald-500"}`}>
                                                    SAVE {discountPercent}%
                                                </div>
                                            )}
                                        </div>
                                        <div className={`pt-3 border-t border-dashed flex justify-between items-center ${isDarkMode ? "border-zinc-800" : "border-zinc-200"}`}>
                                            <span className="text-[10px] text-zinc-500 font-bold uppercase tracking-widest italic">Incl. 18% GST (₹{gst.toLocaleString("en-IN")})</span>
                                            <span className={`text-xs font-black ${isDarkMode ? "text-cyan-400" : "text-emerald-500"}`}>Total: ₹{totalPrice.toLocaleString("en-IN")}</span>
                                        </div>
                                    </div>

                                    <div className="flex gap-3">
                                        <Link
                                            to={`/course/${course.id}`}
                                            className={`flex-1 py-4 rounded-2xl font-black text-xs uppercase tracking-widest text-center transition-all ${isDarkMode ? 'bg-zinc-800 text-white hover:bg-zinc-700' : 'bg-zinc-100 text-black hover:bg-zinc-200'}`}
                                        >
                                            Details
                                        </Link>
                                        <button
                                            onClick={() => owned ? navigate('/lms/courses') : navigate(`/checkout/${course.id}`)}
                                            className={`flex-[2] py-4 rounded-2xl font-black text-xs uppercase tracking-widest transition-all ${owned
                                                ? 'bg-zinc-800 text-zinc-500 cursor-default'
                                                : isDarkMode
                                                    ? 'bg-cyan-600 text-white hover:bg-white hover:text-black shadow-lg shadow-cyan-900/30'
                                                    : 'bg-black text-white hover:bg-blue-600 shadow-xl'
                                                }`}
                                        >
                                            {owned ? 'Enrolled' : 'Enroll Now'}
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

const Certificates = () => {
    const [completedCourses, setCompletedCourses] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const { user } = useAuth();
    const { isDarkMode } = useTheme();

    useEffect(() => {
        const fetchCompleted = async () => {
            try {
                const baseUrl = import.meta.env.VITE_API_BASE_URL || 'http://localhost:5000';
                const res = await axios.get(`${baseUrl}/api/user/completed-courses`, {
                    headers: { Authorization: `Bearer ${user?.token}` }
                });
                if (res.data.success) {
                    setCompletedCourses(res.data.data);
                }
            } catch (err) {
                console.error("Failed to fetch completed courses", err);
            } finally {
                setLoading(false);
            }
        };
        if (user?.token) fetchCompleted();
    }, [user]);

    if (loading) return <div className="p-8 text-center text-[10px] font-bold uppercase tracking-widest text-zinc-500">Scanning for achievements...</div>;

    return (
        <div className="space-y-8">
            <div className="flex flex-col gap-2">
                <h2 className="font-bold text-3xl">Earned Proof</h2>
                <p className="text-zinc-500 font-bold text-[10px]">Your verified career certifications.</p>
            </div>

            {completedCourses.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    {completedCourses.map((course) => (
                        <div key={course._id} className={`p-8 rounded-[3rem] border refractive-border liquid-glass relative overflow-hidden group ${isDarkMode ? 'bg-zinc-900/50 border-white/5' : 'bg-white border-black/5'}`}>
                            <div className="relative z-10">
                                <Award size={32} className="text-cyan-400 mb-6" />
                                <h3 className="font-bold text-2xl mb-2">{course.name}</h3>
                                <p className="text-zinc-500 font-bold text-[8px] mb-8">Issued on {new Date(course.updatedAt).toLocaleDateString()}</p>
                                <button className="px-6 py-3 rounded-xl bg-white text-black font-bold text-[9px] hover:bg-zinc-200 transition-all flex items-center gap-2">
                                    Download PDF <ChevronRight size={14} />
                                </button>
                            </div>
                            {/* Decorative Background Image for Certificate */}
                            {course.certificateImage ? (
                                <img src={course.certificateImage} alt="Certificate Background" className="absolute inset-0 w-full h-full object-cover opacity-5 group-hover:opacity-10 transition-opacity" />
                            ) : (
                                <div className="absolute top-0 right-0 p-8">
                                    <Award size={120} className="text-white/5 -rotate-12" />
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            ) : (
                <div className="h-[50vh] flex items-center justify-center flex-col gap-6 text-center">
                    <div className="h-20 w-20 rounded-full border border-dashed border-zinc-800 flex items-center justify-center">
                        <Award size={32} className="text-zinc-800" />
                    </div>
                    <div className="space-y-2">
                        <h4 className="font-bold text-xl">No Verification Found</h4>
                        <p className="text-zinc-500 font-bold text-[10px] max-w-[250px]">Complete your active tracks to unlock industry-standard certifications.</p>
                    </div>
                    <Link to="/lms/courses" className="px-8 py-4 rounded-xl border border-zinc-800 text-zinc-500 font-bold text-[9px] hover:border-white hover:text-white transition-all">Go to My Tracks</Link>
                </div>
            )}
        </div>
    );
};

const ProfileSettings = () => {
    const { user, login } = useAuth();
    const { isDarkMode } = useTheme();
    const { showToast } = useNotification();
    const [formData, setFormData] = useState({
        name: user?.name || '',
        email: user?.email || '',
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
    });
    const [loading, setLoading] = useState(false);

    const handleUpdate = async (e: React.FormEvent) => {
        e.preventDefault();
        if (formData.newPassword && formData.newPassword !== formData.confirmPassword) {
            showToast('New passwords do not match', 'error');
            return;
        }

        setLoading(true);

        try {
            const baseUrl = import.meta.env.VITE_API_BASE_URL || 'http://localhost:5000';
            const res = await axios.put(`${baseUrl}/api/auth/profile`, formData, {
                headers: { Authorization: `Bearer ${user?.token}` }
            });

            if (res.data.success) {
                showToast('Profile Updated Successfully', 'success');
                // Update local auth context with new data/token
                login(res.data.data);
                setFormData(prev => ({ ...prev, currentPassword: '', newPassword: '', confirmPassword: '' }));
            }
        } catch (err: any) {
            showToast(err.response?.data?.message || 'Update failed', 'error');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="space-y-8">
            <h1 className="font-bold text-4xl">Settings</h1>

            <form onSubmit={handleUpdate} className={`p-8 rounded-[2.5rem] border ${isDarkMode ? 'bg-zinc-900/30 border-white/5' : 'bg-white border-black/5'} space-y-8 max-w-2xl`}>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-2">
                        <label className="text-[8px] font-bold text-zinc-500 block">Display Name</label>
                        <input
                            type="text"
                            value={formData.name}
                            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                            className={`w-full rounded-xl p-4 font-bold text-xs outline-none transition-all ${isDarkMode ? 'bg-zinc-900 border border-white/10 focus:border-cyan-500' : 'bg-zinc-50 border border-black/5 focus:border-black'}`}
                        />
                    </div>
                    <div className="space-y-2">
                        <label className="text-[8px] font-bold text-zinc-500 block">Email Address</label>
                        <input
                            type="email"
                            value={formData.email}
                            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                            className={`w-full rounded-xl p-4 font-bold text-xs outline-none transition-all ${isDarkMode ? 'bg-zinc-900 border border-white/10 focus:border-cyan-500' : 'bg-zinc-50 border border-black/5 focus:border-black'}`}
                        />
                    </div>
                </div>

                <div className="pt-6 border-t border-white/5 space-y-6">
                    <p className="text-[8px] font-bold text-zinc-400 uppercase tracking-widest">Change Password (Optional)</p>

                    <div className="space-y-2">
                        <label className="text-[8px] font-bold text-zinc-500 block">Current Password</label>
                        <input
                            type="password"
                            placeholder="Required for password or email change"
                            value={formData.currentPassword}
                            onChange={(e) => setFormData({ ...formData, currentPassword: e.target.value })}
                            className={`w-full rounded-xl p-4 font-bold text-xs outline-none transition-all ${isDarkMode ? 'bg-zinc-900 border border-white/10 focus:border-cyan-500' : 'bg-zinc-50 border border-black/5 focus:border-black'}`}
                        />
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="space-y-2">
                            <label className="text-[8px] font-bold text-zinc-500 block">New Password</label>
                            <input
                                type="password"
                                value={formData.newPassword}
                                onChange={(e) => setFormData({ ...formData, newPassword: e.target.value })}
                                className={`w-full rounded-xl p-4 font-bold text-xs outline-none transition-all ${isDarkMode ? 'bg-zinc-900 border border-white/10 focus:border-cyan-500' : 'bg-zinc-50 border border-black/5 focus:border-black'}`}
                            />
                        </div>
                        <div className="space-y-2">
                            <label className="text-[8px] font-bold text-zinc-500 block">Confirm New Password</label>
                            <input
                                type="password"
                                value={formData.confirmPassword}
                                onChange={(e) => setFormData({ ...formData, confirmPassword: e.target.value })}
                                className={`w-full rounded-xl p-4 font-bold text-xs outline-none transition-all ${isDarkMode ? 'bg-zinc-900 border border-white/10 focus:border-cyan-500' : 'bg-zinc-50 border border-black/5 focus:border-black'}`}
                            />
                        </div>
                    </div>
                </div>

                <button
                    disabled={loading}
                    className={`px-10 py-5 rounded-2xl bg-cyan-500 text-white font-bold text-[10px] transition-all hover:bg-cyan-400 disabled:opacity-50`}
                >
                    {loading ? 'Processing...' : 'Save Changes'}
                </button>
            </form>
        </div>
    );
};

export default Dashboard;
